package J0321;

public class OperatorExample6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean bPlay = true;

		System.out.println(bPlay);

		bPlay = !bPlay; // ! :not 연산자
		System.out.println(bPlay);

		bPlay = !bPlay;
		System.out.println(bPlay);
	}

}
